SELECT DROP_OUT_USER_ID, DROP_OUT_DATE, DROP_OUT_REASON, DROP_OUT_REASON_CODE, DROP_OUT_PERSON, (SELECT COUNT(*) FROM DROP_OUT_USER WHERE DROP_OUT_USER_ID IS NOT NULL
AND DROP_OUT_USER_ID = #{searchValue}
AND DROP_OUT_DATE BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d')
AND DROP_OUT_REASON_CODE = #{searchValue1} ) AS DROP_OUT_COUNT FROM DROP_OUT_USER WHERE DROP_OUT_USER_ID IS NOT NULL
AND DROP_OUT_USER_ID = #{searchValue}
AND DROP_OUT_DATE BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d')
AND DROP_OUT_REASON_CODE = #{searchValue1}
AND DROP_OUT_PERSON LIKE '관리자%'
AND DROP_OUT_PERSON LIKE '본인'
AND DROP_OUT_PERSON LIKE '%%'
ORDER BY DROP_OUT_DATE DESC LIMIT #{rowStartNumber}, #{pageGroupSize}

SELECT COUNT(*) FROM DROP_OUT_USER WHERE DROP_OUT_USER_ID IS NOT NULL
AND DROP_OUT_USER_ID = #{searchValue}
AND DROP_OUT_DATE BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d')
